import { StatusBar } from 'expo-status-bar';
import React, {useState, useEffect} from 'react';
import { StyleSheet, Text, View, ActivityIndicator, SafeAreaView, FlatList,Image } from 'react-native';

export default function App() {
  const [data, setdata]=useState([]);
  const [isLoading, setisLoading]=useState(true);

  useEffect(() => {
    getListPhotos();
    return () => {

    }
  }, [])

  const getListPhotos = () =>{
    const apiURL = 'https://disease.sh/v3/covid-19/countries/vn';
    fetch(apiURL)
    .then((res) => res.json()).then((json)=>{
      setdata(json)
    }).catch((error)=>{
      console.log('Error:', error);
    }).finally(()=>setisLoading(false))
  }

  const renderItem=({item, index})=>{
    return(
     <View style={styles.item} >

     <View style={styles.wraptext} >
     <Text>{item.country}</Text>
     </View>
     </View>
     )
   }
   return (
   <SafeAreaView style={styles.container}>

   <FlatList
   style={styles.list}
   data={data}
   renderItem={renderItem}

   />

   </SafeAreaView>
   );
 };

 const styles = StyleSheet.create({
  container: {
    flex: 1,

  },

  list: {
    flex: 1,
    padding: 8

  },

  item: {
    flexDirection: 'row',
    marginTop: 8,
    padding: 5,
    shadowColor: '#000',
    shadowRadius:4,
    shadowOpacity: 0.25
  },

  image: {
    width: 100,
    height: 150
  },

  wraptext: {
    flex: 1,
    justifyContent: 'center',
    marginLeft:8
  }
});
